import re
from transaction import commit

if evt.summary.startswith('threshold of su association status '):
    suinfo = ""
    if device:
        for su in device.trangoSubscriberUnit():
           if su.id == evt.component:
              if su.suRemarks:
                 suinfo = su.id + " (" + su.suRemarks + ")"
              else:
                 suinfo = su.id
              m = re.search("threshold of [^:]+: current value ([\d\.]+)", evt.message)
              if not m: continue
              currentvalue = (int(float(m.groups()[0])))

              # manually set SU status indicator if does not match current value
              if su.suAssociation != currentvalue:
                 su.suAssociation = currentvalue
                 commit()

              #only create the event if it is a VIP customer so device does not turn yellow from home customer going down
              if su.suRemarks.startswith('vip-'):
                 if evt.severity > 0:
                     evt.summary = "subscriber unit " + suinfo + " is DOWN "
                     evt.severity=4
                 else:
                     evt.summary = "subscriber unit " + suinfo + " is UP "
                 evt.message = evt.summary
              else:
                 evt._action = 'drop'
              break
elif evt.summary.startswith('threshold of signal strength '):
    suinfo = ""
    if device:
        for su in device.trangoSubscriberUnit():
           if su.id == evt.component:
              if su.suRemarks:
                 suinfo = su.id + " (" + su.suRemarks + ")"
              else:
                 suinfo = su.id
              m = re.search("threshold of [^:]+: current value ([-?\d\.]+)", evt.message)
              if not m: continue
              currentvalue = float(m.groups()[0])

              #only create the event if it is a VIP customer so device does not turn yellow from home customer issue
              #also ignore signal -120dB because a subscriber down event will be generated
              if (su.suRemarks.startswith('vip-') and (currentvalue > -120.0)):
                 if evt.severity > 0:
                     evt.summary = "subscriber unit " + suinfo + " has BAD SIGNAL STRENGTH (" + str(currentvalue) + "dB)"
                     evt.severity=4
                 else:
                     evt.summary = "subscriber unit " + suinfo + " signal strength has returned to normal (" + str(currentvalue) + "dB)"
                 evt.message = evt.summary
                 evt.eventKey = "signal strength"
              else:
                 evt._action = 'drop'
              break

