from transaction import commit

evt.component = "suid" + str(getattr(evt, 'suID', ''))
suinfo = ""
if device:
    for su in device.trangoSubscriberUnit():
       if su.id == evt.component:
          if su.suRemarks:
             suinfo = su.id + " (" + su.suRemarks + ")"
          else:
             suinfo = su.id

          # manually set SU status indicator if not already down
          if su.suAssociation != 3:
             su.suAssociation = 3
             commit()

          #only create the event if it is a VIP customer so device does not turn yellow from home customer going down
          if su.suRemarks.startswith('vip-'):
             evt.summary = "subscriber unit " + suinfo + " is DOWN "
             evt.message = evt.summary
             evt.severity=4
          else:
             evt._action = 'drop'
          break

from transaction import commit

evt.component = "suid" + str(getattr(evt, 'suID', ''))
suinfo = ""
if device:
    for su in device.trangoSubscriberUnit():
       if su.id == evt.component:
          if su.suRemarks:
             suinfo = su.id + " (" + su.suRemarks + ")"
          else:
             suinfo = su.id

          # manually set SU status indicator to up if not already
          if su.suAssociation != 1:
             su.suAssociation = 1
             commit()

          #only create the event if it is a VIP customer so device does not turn yellow from home customer going down
          if su.suRemarks.startswith('vip-'):
             evt.summary = "subscriber unit " + suinfo + " is UP "
             evt.message = evt.summary
          else:
             evt._action = 'drop'
          break
